arguments.command:
python3 - <<'PY'
import urllib.request, urllib.parse, xml.etree.ElementTree as ET, re
from collections import defaultdict

feeds={
 'g_us_tech':'https://news.google.com/rss/headlines/section/topic/TECHNOLOGY?hl=en-US&gl=US&ceid=US:en',
 'g_us_bus':'https://news.google.com/rss/headlines/section/topic/BUSINESS?hl=en-US&gl=US&ceid=US:en',
 'g_world_tech':'https://news.google.com/rss/headlines/section/topic/TECHNOLOGY?hl=en&gl=GB&ceid=GB:en',
 'g_world':'https://news.google.com/rss?hl=en-US&gl=US&ceid=US:en',
 'g_ai_search':'https://news.google.com/rss/search?q='+urllib.parse.quote('AI OR "artificial intelligence" OR OpenAI OR Anthropic OR Gemini OR NVIDIA OR "AI regulation" when:1d')+'&hl=en-US&gl=US&ceid=US:en',
}

weights={'g_us_tech':1.4,'g_us_bus':1.1,'g_world_tech':1.3,'g_world':1.0,'g_ai_search':1.7}

ai_kw=['ai','artificial intelligence','openai','anthropic','gemini','deepmind','copilot','llm','chatgpt','nvidia','ai chip','ai model','ai agent','midjourney','stability ai','xai']

def norm(t):
    t=t.strip()
    t=re.sub(r'\s*[-|‚Äì‚Äî]\s*[^-|‚Äì‚Äî]+$','',t)
    return t

def is_ai(t):
    x=t.lower()
    return any(k in x for k in ai_kw)

agg=defaultdict(lambda:{'score':0,'src':set(),'title':'','links':[]})
for name,url in feeds.items():
    try:
        data=urllib.request.urlopen(url,timeout=20).read()
    except Exception:
        continue
    root=ET.fromstring(data)
    items=root.findall('./channel/item')[:40]
    for i,it in enumerate(items,1):
        title=norm((it.findtext('title') or '').strip())
        link=(it.findtext('link') or '').strip()
        if not title or not is_ai(title):
            continue
        key=re.sub(r'[^a-z0-9\u4e00-\u9fff]+',' ',title.lower()).strip()
        rec=agg[key]
        rec['score'] += weights[name]*(1/(0.7+0.12*i))
        rec['src'].add(name)
        if not rec['title']:
            rec['title']=title
        rec['links'].append(link)

rows=[]
for k,v in agg.items():
    rows.append((v['score'],len(v['src']),v['title'],list(v['src'])[:],v['links'][0]))
rows.sort(reverse=True)
for idx,r in enumerate(rows[:20],1):
    print(f"{idx}\t{r[2]}\t{r[0]:.2f}\t{r[1]}\t{','.join(sorted(r[3]))}\t{r[4]}")
PY

